import "./App.css";
import Popup from "./Pop-up.js";
import EscapeListener from "./EscapeListener.js";
import MainCell from "./MainCell.js";
import { useState, useEffect } from "react";

const PopupCell = (props) => {
  return (
    <div className="main_cell" onClick={() => props.togglePopup(props.item.id)}>
      <div className="container">
        <img src={props.item.thumbnail.small} className="imagine" />
        <div class="overlay">
          <div class="text">Learn More</div>
        </div>
      </div>
      <div className="popup_title">{props.item.title}</div>
      <div className="object_content">{props.item.content}</div>
      <br></br>
      <div className="row">
        <div className="avatar">{props.item.author.name}</div>
        <div className="avatar">{props.item.author.role}</div>
      </div>
      <br></br>
    </div>
  );
};

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [objectID, setID] = useState(null);

  const fetchUrl =
    "https://my-json-server.typicode.com/Codeinwp/front-end-internship-api/posts";
  const getData = () => fetch(`${fetchUrl}`).then((res) => res.json());

  EscapeListener(() => {
    setIsOpen(false);
  });

  useEffect(() => {
    getData().then((data) => setData(data));
  }, []);

  const togglePopup = (id) => {
    setIsOpen(!isOpen);
    setID(id);
  };

  return (
    <div className="row">
      {isOpen && (
        <Popup
          content={
            <div>
              {data?.map((item) => {
                if (item.id == objectID) {

                }
              })}
            </div>
          }
          handleClose={togglePopup}
        />
      )}
      <div className="column">
        {data?.map((item, i) => {
          if (i % 2 == 0) {
            return <MainCell item={item} togglePopup={togglePopup} />;
          }
        })}
      </div>
      <div className="column">
        {data?.map((item, i) => {
          if (i % 2 == 1)
            return <MainCell item={item} togglePopup={togglePopup} />;
        })}
      </div>
    </div>
  );
}

export default App;
