import "./App.css";
import { useState, useEffect } from "react";

const Popup = (props) => {
  return (
    <div className="popup-box">
      <div className="box">
        <span className="close-icon" onClick={props.handleClose}>
          Close
        </span>
        {props.content}
      </div>
    </div>
  );
};

const Container = (data) => {
  return (
    <div>
      <ul>
        <li>
          <img src={data.thumbnail.small} />
        </li>
        <h2>
          <li>{data.title}</li>
        </h2>
        <br></br>
        <div className="object_content">
          <li>{data.content}</li>
        </div>
        <br></br>
      </ul>
    </div>
  );
};

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [objectID, setID] = useState(null);

  const fetchUrl =
    "https://my-json-server.typicode.com/Codeinwp/front-end-internship-api/posts";
  const getData = () => fetch(`${fetchUrl}`).then((res) => res.json());

  useEffect(() => {
    getData().then((data) => setData(data));
  }, []);

  const togglePopup = (id) => {
    setIsOpen(!isOpen);
    setID(id);
  };

  return (
    <div className="row">
      {isOpen && (
        <Popup
          content={
            <div>
              {data?.map((item) => {
                if (item.id == objectID) {
                  return (
                  <Container data={item} />);
                }
              })}
            </div>
          }
          handleClose={togglePopup}
        />
      )}
      <div className="container1">
        {data?.map((item, i) => {
          if (i % 2 == 0) {
            return (
              <div className="lista" onClick={() => togglePopup(item.id)}>
                <ul>
                  <li>
                    <img src={item.thumbnail.small} />
                  </li>
                  <h2>
                    <li>{item.title}</li>
                  </h2>
                  <br></br>
                  <div className="object_content">
                    <li>{item.content}</li>
                  </div>
                  <br></br>
                </ul>
              </div>
            );
          }
        })}
      </div>
      <div className="container2">
        {data?.map((item, i) => {
          if (i % 2 == 1)
            return (
              <div className="lista" onClick={() => togglePopup(item.id)}>
                <ul>
                  <li>
                    <img src={item.thumbnail.small} />
                  </li>
                  <h2>
                    <li>{item.title}</li>
                  </h2>
                  <br></br>
                  <div className="object_content">
                    <li>{item.content}</li>
                  </div>
                  <br></br>
                </ul>
              </div>
            );
        })}
      </div>
    </div>
  );
}

export default App;
