import "./App.css";
import { useState, useEffect } from "react";

const Popup = (props) => {
  return (
    <div className="popup-box">
      <div className="box">
        <span className="close-icon" onClick={props.handleClose}>
          Close
        </span>
        {props.content}
      </div>
    </div>
  );
};

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const [objectID, setID] = useState(null);

  const fetchUrl =
    "https://my-json-server.typicode.com/Codeinwp/front-end-internship-api/posts";
  const getData = () => fetch(`${fetchUrl}`).then((res) => res.json());

  useEffect(() => {
    getData().then((data) => setData(data));
  }, []);

  const togglePopup = (id) => {
    setIsOpen(!isOpen);
    setID(id);
  };

  return (
    <div className="row">
      {isOpen && (
        <Popup
          content={
            <div>
              {data?.map((item) => {
                if (item.id == objectID) {
                  return (
                    <div className="popup_cell">
                      <img src={item.thumbnail.large} />
                      <h2>{item.title}</h2>
                      <br></br>
                      <div className="object_content">{item.content}</div>
                      <br></br>
                    </div>
                  );
                }
              })}
            </div>
          }
          handleClose={togglePopup}
        />
      )}
      <div className="container1">
        {data?.map((item, i) => {
          if (i % 2 == 0) {
            return (
              <div>
                <div className="main_cell" onClick={() => togglePopup(item.id)}>
                  <img src={item.thumbnail.small} />
                  <h2>{item.title}</h2>
                  <br></br>
                  <div className="object_content">{item.content}</div>
                  <br></br>
                </div>
              </div>
            );
          }
        })}
      </div>
      <div className="container2">
        {data?.map((item, i) => {
          if (i % 2 == 1)
            return (
              <div className="main_cell" onClick={() => togglePopup(item.id)}>
                <img src={item.thumbnail.small} />
                <h2>{item.title}</h2>
                <br></br>
                <div className="object_content">{item.content}</div>
                <br></br>
              </div>
            );
        })}
      </div>
    </div>
  );
}

export default App;
