import logo from "./logo.svg";
import "./App.css";
import { useState, useEffect } from "react";

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const fetchUrl =
    "https://my-json-server.typicode.com/Codeinwp/front-end-internship-api/posts";
  const getData = () => fetch(`${fetchUrl}`).then((res) => res.json());

  useEffect(() => {
    getData().then((data) => setData(data));
  }, []);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  if (isOpen) {
    return (
      <div className="row">
        <div className="container1">
          {data?.map((item, i) => {
            if (i % 2 == 0) {
              return (
                <div className="lista">
                  <ul>
                    <li>
                      <img src={item.thumbnail.small} />
                    </li>
                    <h2>
                      <li>{item.title}</li>
                    </h2>
                    <br></br>
                    <div className="object_content">
                      <li>{item.content}</li>
                    </div>
                    <br></br>
                    <input
                      type="button"
                      value="Click to Open Popup"
                      onClick={togglePopup}
                    />
                    <br></br>
                  </ul>
                </div>
              );
            }
          })}
        </div>
        <div className="container2">
          {data?.map((item, i) => {
            if (i % 2 == 1)
              return (
                <div className="lista">
                  <ul>
                    <li>
                      <img src={item.thumbnail.small} />
                    </li>
                    <h2>
                      <li>{item.title}</li>
                    </h2>
                    <br></br>
                    <div className="object_content">
                      <li>{item.content}</li>
                    </div>
                    <br></br>
                    <input
                      type="button"
                      value="Click to Open Popup"
                      onClick={togglePopup}
                    />
                    <br></br>
                  </ul>
                </div>
              );
          })}
        </div>
      </div>
    );
  }
  else {
    return (
      <div oncl>
        <p>
          Pop-up
        </p>
      </div>
    );
  }
}

export default App;
