import "./App.css";
import { useState, useEffect } from "react";

const Popup = (props, id) => {
  return (
    <div className="popup-box">
      <div className="box">
        <span className="close-icon" onClick={props.handleClose}>
          X
        </span>
        {props.content}
      </div>
    </div>
  );
};

function App() {
  const [data, setData] = useState(null);
  const [isOpen, setIsOpen] = useState(false);
  const fetchUrl =
    "https://my-json-server.typicode.com/Codeinwp/front-end-internship-api/posts";
  const getData = () => fetch(`${fetchUrl}`).then((res) => res.json());

  useEffect(() => {
    getData().then((data) => setData(data));
  }, []);

  const togglePopup = () => {
    setIsOpen(!isOpen);
  };

  return (
    <div className="row">
      {data?.map((item, i) => {
        {
          isOpen && (
            <Popup
              content={
                <div>
                  <ul>
                    <li>
                      <img src={item.thumbnail.small} />
                    </li>
                    <p>id = {item.id}</p>
                    <h2>
                      <li>{item.title}</li>
                    </h2>
                    <br></br>
                    <div className="object_content">
                      <li>{item.content}</li>
                    </div>
                    <br></br>
                  </ul>
                </div>
              }
              handleClose={togglePopup}
            />
          );
        }
      })}
      <div className="container1">
        {data?.map((item, i) => {
          if (i % 2 == 0) {
            return (
              <div className="lista" onClick={togglePopup}>
                <ul>
                  <li>
                    <img src={item.thumbnail.small} />
                  </li>
                  <p>id = {item.id}</p>
                  <h2>
                    <li>{item.title}</li>
                  </h2>
                  <br></br>
                  <div className="object_content">
                    <li>{item.content}</li>
                  </div>
                  <br></br>
                </ul>
              </div>
            );
          }
        })}
      </div>
      <div className="container2">
        {data?.map((item, i) => {
          if (i % 2 == 1)
            return (
              <div className="lista" onClick={togglePopup}>
                <ul>
                  <li>
                    <img src={item.thumbnail.small} />
                  </li>
                  <p>id = {item.id}</p>
                  <h2>
                    <li>{item.title}</li>
                  </h2>
                  <br></br>
                  <div className="object_content">
                    <li>{item.content}</li>
                  </div>
                  <br></br>
                </ul>
              </div>
            );
        })}
      </div>
    </div>
  );
}

export default App;
